mapper.select:
SELECT t.com_id AS comId, t.com_name AS comName, t.org_code AS orgCode, t.business_num AS businessNum, t.regis_address AS regisAddress, t.com_address AS comAddress, t.legal_person AS legalPerson, t.economic_type AS economicType, t.regis_capital AS regisCapital, t.skill_leader AS skillLeader, t.created, t.updated, t.logo FROM mishu.`tb_company` t where t.regis_address = #{regisAddress}
and (
t.range like CONCAT('%',#{list},'%')
)
and t.range = null
order by t.px desc
