mapper.select:
SELECT mian.pkid as id, mian.title, bids.f_candidate as oneName, bids.f_quote as oneOffer, mian.pub_date as opendate, mian.source, mian.provice_code as provice, mian.city_code as city, mian.snatch_id as snatchId, , mian.nt_category as type, bids.pb_mode as pbMode FROM tb_nt_mian_${proviceCode} mian INNER JOIN tb_nt_bids_${proviceCode} bids ON mian.`pkid` = bids.`nt_id` WHERE 1 = 1 AND mian.`pub_date` >= DATE_SUB(NOW(),INTERVAL 100 DAY) AND mian.`pub_date` <= NOW()
and mian.title like CONCAT('%',#{title},'%')
and mian.provice_code=#{proviceCode}
and mian.nt_category=#{type}
or mian.city_code=#{cityCodeList}
and mian.biness_type=#{projectType}
and bids.f_quote >= #{projSumStart}
and bids.f_quote <= #{projSumEnd}
ORDER BY mian.pub_date DESC

SELECT mian.pkid AS id, mian.`title`, mian.snatch_id as snatchId, mian.`pub_date` AS opendate, tenders.qua_name AS certificate, mian.source, mian.provice_code as provice, mian.city_code as city, , mian.nt_category AS `type`, (SELECT t.`name` FROM dic_common t WHERE t.`code` = tenders.`pb_mode` AND t.`type` = '${pdModeType}') AS pdmode FROM tb_nt_mian_${proviceCode} mian JOIN tb_nt_tenders_${proviceCode} tenders ON mian.pkid = tenders.nt_id WHERE 1 = 1 AND mian.`pub_date` >= DATE_SUB(NOW(),INTERVAL 100 DAY) AND mian.`pub_date` <= NOW()
and mian.title like CONCAT('%',#{title},'%')
and mian.provice_code=#{proviceCode}
or mian.city_code=#{cityCodeList}
or mian.pb_mode=#{pbModeList}
and mian.biness_type=#{projectType}
and mian.nt_category=#{type}
mian.pkid in (select regex.nt_id from tb_nt_regex_qua regex where 1=1
or regex.qua_regex=#{regexList}
)
ORDER BY mian.`pub_date` DESC

select id from rel_qua_grade where qua_code=#{zzdj1} and grade_code=#{zzdj2}

  or qua_code=#{zz2dj1} and grade_code=#{zzdj2}


  or qua_code=#{zz3dj1} and grade_code=#{zz3dj2}
